first_divide_nastole3kg = buffer(pompki_nastole3kg_cross, numel(pompki_nastole3kg_cross)/10); 
counter_nastole3kg = zeros(10,1)
first_divide_bezpodparcia3kg = buffer(pompki_bezpodparcia3kg_cross, numel(pompki_bezpodparcia3kg_cross)/10); 
