xlabel('Czas [s]')
ylabel('Ilość przekroczeń zer')
ylim([0 120])
grid on;



% Fourier Transform
L_lo = length(nastole3kg);
L_hi = length(bezpodparcia3kg);

fs = 1000; %sampling frequency
f_lo = fs*(0:(L_lo/2))/L_lo; % FFT frequency from 0 to L/2 data length
f_hi = fs*(0:(L_hi/2))/L_hi;

%compute two sided spectrum (-Fmax:Fmax)
p1 = fft(nastole3kg);
p2 = fft(bezpodparcia3kg);

%divive by L for normalization of the power of the output 
p1 = abs(p1/L_lo);
p2 = abs(p2/L_hi);

% compute signal sided spectrum
p1=p1(1:L_lo/2+1);
p1(2:end-1) = 2*p1(2:end-1);
p2=p2(1:L_hi/2+1);
p2(2:end-1) = 2*p2(2:end-1);

%Plotting FFT
figure()
subplot(1, 2, 1)
plot(f_lo, p1)
xlabel('Frequency[Hz]')
ylabel('Intensity')
sgtitle('FFT')
legend('Z podparciem z obciążeniem 3kg')
ylim([0 14])
grid on;

subplot(1, 2, 2)
plot(f_hi, p2)
xlabel('Frequency[Hz]')
ylabel('Intensity')
legend('Bez podparcia z obciążeniem 3kg')
ylim([0 14])
grid on;
%% Rectification
nastole3kg = abs(nastole3kg);
bezpodparcia3kg = abs(bezpodparcia3kg);
%% RMS Mean Power of signal
envelope_lo = zeros(L_lo, 1);
envelope_hi = zeros(L_hi, 1);
window = 100;
envelope_lo = sqrt(movmean(nastole3kg.^2, window));
envelope_hi = sqrt(movmean(bezpodparcia3kg.^2, window));
%% Normalization with MVC
MVC_lo = max(nastole3kg);
MVC_lo_normalised = zeros(L_lo, 1);
MVC_lo_normalised = envelope_lo./MVC_lo.*100;

MVC_hi = max(bezpodparcia3kg);
MVC_hi_normalised = zeros(L_hi, 1);
MVC_hi_normalised = envelope_hi./MVC_hi.*100;

figure()
plot(nastole3kg)
hold on;
plot(envelope_lo, 'r')
xlabel("Time [ms]")
ylabel("Amplitude [μV]")
xlim([0 30000])
title('Normalizacja MVC (nastole3kg)')
legend('EMG signal', 'Normalized signal')

figure()
plot(bezpodparcia3kg)
hold on;
plot(envelope_hi, 'r')
xlabel("Time [ms]")
ylabel("Amplitude [μV]")
xlim([0 25000])
title('Normalizacja MVC (bezpodparcia3kg)')
legend('EMG signal', 'Normalized signal')
